import React, { useState, useEffect } from 'react'
import styles from './Weather.module.css'

const Weather = () => {
  const [city, setCity] = useState('')
  const [weather, setWeather] = useState(null)
  const [error, setError] = useState(null)
  const [loading, setLoading] = useState(false)
  const [theme, setTheme] = useState('light')

  const apiKey = '908232d26530bd4b21394cc29a269929'

  const cityHandler = (e) => {
    setCity(e.target.value)
    const newCity = e.target.value
    localStorage.setItem('city', newCity)
  }
  const themeSwitcher = () => {
    if (theme === 'light') {
      setTheme('dark')
      const newTheme = theme
      localStorage.setItem('theme', newTheme)
    } else {
      setTheme('light')
      const newTheme = theme
      localStorage.setItem('theme', newTheme)
    }
  }
  const searchCity = async () => {
    try {
      setError(null)
      setWeather(null)
      setLoading(true)

      const res = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`
      )

      const data = await res.json()

      if (data.cod !== 200) {
        setError(data.message)
        return
      }

      setWeather(data)
      const newWeather = weather
      localStorage.setItem('weather', newWeather)
    } catch (err) {
      setError('Something went wrong')
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    const saveCity = localStorage.getItem('city')
    const saveWeather = localStorage.getItem('weather')
    const saveTheme = localStorage.getItem('theme')

    if (saveCity) setCity(saveCity)
    if (saveWeather) setWeather(saveWeather)
    if (saveTheme) setTheme(saveTheme)
  }, [])

  return (
    <div className={theme === 'light' ? styles.wrapper : styles.wrapperDark}>
      <h1 className={styles.title}>üå§Ô∏è Weather App</h1>

      <div className={styles.search}>
        <input
          type="text"
          value={city}
          onChange={cityHandler}
          placeholder="Enter city..."
          className={styles.input}
        />
        <button onClick={searchCity} className={styles.button}>
          Find
        </button>
        <button onClick={searchCity} className={styles.button}>
          Refresh
        </button>
        <button onClick={themeSwitcher}>Switch theme</button>
      </div>

      {error && <p className={styles.error}>{error}</p>}
      {loading && <p>Loading...</p>}
      {weather && (
        <div className={styles.card}>
          <h2 className={styles.city}>
            {weather.name}, {weather.sys.country}
          </h2>
          <img
            src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt={weather.weather[0].description}
            className={styles.icon}
          />
          <p className={styles.temp}>{Math.round(weather.main.temp)}¬∞C</p>
          <p className={styles.feels}>
            Feels like: {Math.round(weather.main.feels_like)}¬∞C
          </p>
          <p className={styles.desc}>{weather.weather[0].description}</p>

          <div className={styles.details}>
            <p>
              üå°Ô∏è Min: {Math.round(weather.main.temp_min)}¬∞C | Max:{' '}
              {Math.round(weather.main.temp_max)}¬∞C
            </p>
            <p>üíß Humidity: {weather.main.humidity}%</p>
            <p>üìä Pressure: {weather.main.pressure} hPa</p>
            <p>
              üå¨Ô∏è Wind: {weather.wind.speed} m/s ({weather.wind.deg}¬∞)
            </p>
            <p>‚òÅÔ∏è Clouds: {weather.clouds.all}%</p>
            <p>üå´Ô∏è Visibility: {weather.visibility / 1000} km</p>
            <p>
              üìç Coords: [{weather.coord.lat}, {weather.coord.lon}]
            </p>
            <p>
              üåÖ Sunrise:{' '}
              {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
            </p>
            <p>
              üåá Sunset:{' '}
              {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
            </p>
          </div>
        </div>
      )}
    </div>
  )
}

export default Weather
